import React, { useState } from "react";
import styles from "../styles/Input.module.css";

function Input({ handleSubmit, ipTyped }) {
  const [ip, setIp] = useState(ipTyped);

  const submitIp = (e) =>{
    (e) => {
      e.preventDefault();
      handleSubmit(ip);
    }
  }

  return (
    <div className={styles.input}>
      <div className="text-center">
        <h1>IP Address Tracker</h1>
      </div>
      <div className="">
        <form
          className="flex flex-row"
          onSubmit={(e) => {
            e.preventDefault();
            handleSubmit(ip);
          }}
        >
          <input
            type="text"
            name="ip"
            id="ip"
            placeholder={ipTyped}
            className="rounded-l-md px-6 py-2 h-10 flex-grow"
            onChange={(e) => {
              setIp(e.target.value);
              console.log(ip);
            }}
            value={ip}
          />
          <button className="bg-black py-2 rounded-r-md text-white h-10 w-8">
            S
          </button>
        </form>
      </div>
    </div>
  );
}

export default Input;
