import React, { useState } from "react";
import Head from "next/head";
import Input from "../components/Input.js";
import IpCard from "../components/IpCard.js";
import MapUp from "../components/MapIp.js";

function Home() {
  const [ip, setIp] = useState("");
  const [ipData, setIpData] = useState("");

  const handleSubmit = async (ip) => {
    await setIp(ip);
    console.log(ip);
    getIpDetails(ip);
  };

  const getIpDetails = async (ip) => {
    const res = await fetch(
      `https://geo.ipify.org/api/v1?apiKey=at_Ycuf9ssDWYjHU8yM3Xx7cs7R0Vv3B&ipAddress=${ip}`
    );
    const json = await res.json();
    setIpData(json);
  };

  return (
    <div>
      <Head>
        <title>IP Tracker</title>
        <meta name="viewport" content="initial-scale=1.0, width=device-width" />
        <script
          src="https://unpkg.com/leaflet@1.7.1/dist/leaflet.js"
          integrity="sha512-XQoYMqMTK8LvdxXYG3nZ448hOEQiglfqkJs1NOQV44cWnUrBc8PkAOcXy20w0vlaXaVUearIOBhiXZ5V3ynxwA=="
          crossorigin=""
        ></script>
        <link
          rel="stylesheet"
          href="https://unpkg.com/leaflet@1.7.1/dist/leaflet.css"
          integrity="sha512-xodZBNTC5n17Xt2atTPuE1HxjVMSvLVW9ocqUKLsCC5CXdbqCmblAshOMAS6/keqq/sMZMZ19scR4PsZChSR7A=="
          crossorigin=""
        />
      </Head>
      <div className="Home">
        <Input handleSubmit={handleSubmit} ipTyped={ip} />
        <IpCard ipDetails={ipData} />
        <Map cordinates={ipData.location} />
      </div>
    </div>
  );
}

export default Home;
