import React, { useState } from "react";
import styles from "../styles/Input.module.css";

function Input({ handleSubmit, ipTyped }) {
  const [ip, setIp] = useState(ipTyped);

  const submitIp = (e) => {
    e.preventDefault();
    handleSubmit(ip);
  };
  const changeInput = (e) => {
    setIp(e.target.value);
    // console.log(ip);
  };

  return (
    <div className={styles.input}>
      <div className="text-center">
        <h1>IP Address Tracker</h1>
      </div>
      <div className="">
        <form className="flex flex-row" onSubmit={submitIp}>
          <input
            type="text"
            name="ip"
            id="ip"
            placeholder="Search for any IP address or domain"
            className="rounded-l-md px-6 py-2 h-16 flex-grow"
            onChange={changeInput}
            value={ip}
          />
          <button className="bg-black py-2 rounded-r-md text-white h-18 w-16 text-center align-middle">
            <img
              src="/images/icon-arrow.svg"
              alt="send"
              className="self-center ml-3"
            />
          </button>
        </form>
      </div>
    </div>
  );
}

export default Input;
